import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const WalletConnect = ({ onConnect }) => {
  const [selectedRole, setSelectedRole] = useState("client");
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Check if Freighter is available
      if (typeof window !== "undefined" && window.freighter) {
        const publicKey = await window.freighter.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else if (typeof window !== "undefined" && window.stellar) {
        const publicKey = await window.stellar.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else {
        // Allow manual entry as a fallback for testing without the browser extension
        const useFallback = window.confirm(
          "Freighter or Stellar Wallet not installed. Would you like to enter a mock wallet address to continue?"
        );
        if (useFallback) {
          const mock = prompt("Enter a Stellar public key (or leave blank for a generated mock):");
          let publicKey = mock;
          if (!publicKey) {
            // generate a fake-looking public key for UI/testing only
            publicKey = "G" + Math.random().toString(36).substring(2, 56).toUpperCase().padEnd(56, "A").slice(0, 56);
          }
          onConnect(publicKey, selectedRole);
          navigate("/");
        }
      }
    } catch (error) {
      console.error("Wallet connection failed:", error);
      alert("Failed to connect wallet: " + error.message);
    }
  };

  return (
    <div className="wallet-page-container">
      <div className="wallet-connect-card glass-card">
        <h1 style={{ marginBottom: "0.5rem", fontSize: "2.5rem" }}>üöÄ FairDeal</h1>
        <p style={{ color: "var(--text-muted)", marginBottom: "2rem" }}>Secure Escrow for Freelancers</p>

        <div className="form-group">
          <label>I am a:</label>
          <div className="role-selector">
            <button
              className={`role-btn ${selectedRole === "client" ? "active" : ""}`}
              onClick={() => setSelectedRole("client")}
            >
              üë®‚Äçüíº Client
            </button>
            <button
              className={`role-btn ${selectedRole === "freelancer" ? "active" : ""}`}
              onClick={() => setSelectedRole("freelancer")}
            >
              üë®‚Äçüíª Freelancer
            </button>
          </div>
        </div>

        <button className="btn btn-primary" onClick={connectWallet} style={{ width: "100%", padding: "1rem" }}>
          Connect {selectedRole === "client" ? "as Client" : "as Freelancer"}
        </button>

        <p style={{ marginTop: "2rem", fontSize: "0.9rem", color: "var(--text-muted)" }}>
          New to Stellar?{" "}
          <a href="https://freighter.app" target="_blank" rel="noopener noreferrer" style={{ color: "var(--primary)" }}>
            Install Freighter Wallet
          </a>
        </p>
      </div>
    </div>
  );
};

export default WalletConnect;
