import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const CreateJob = ({ wallet }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [formData, setFormData] = useState({
    freelancerAddress: "",
    amount: "",
    deadlineDays: "7",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    try {
      // Validation
      if (!formData.freelancerAddress) {
        throw new Error("Freelancer address is required");
      }
      if (!formData.amount || formData.amount <= 0) {
        throw new Error("Amount must be greater than 0");
      }
      if (!formData.description) {
        throw new Error("Description is required");
      }

      // Check if wallet is connected
      if (!wallet || !wallet.publicKey) {
        throw new Error("Please connect your wallet first");
      }

      // Check if Freighter is available
      if (!window.freighter) {
        throw new Error("Freighter wallet not found. Please install Freighter extension.");
      }

      console.log("Creating job with:", formData);
      console.log("Client wallet:", wallet.publicKey);

      // Build and sign transaction with Freighter
      setSuccess("â³ Preparing transaction... Please approve in your wallet.");

      try {
        // Request transaction signature from Freighter
        // For MVP: We'll create a test transaction to prove wallet integration works
        // In production: This would invoke the Soroban smart contract's create_job function
        
        const signedTx = await window.freighter.signTransaction(
          await buildJobCreationTransaction(),
          "Test SDF Network ; September 2015"  // testnet passphrase
        );

        console.log("âœ… Transaction signed successfully!");
        
        // Simulate job creation (in production, submit signedTx to network)
        const jobId = Math.floor(Math.random() * 10000);

        setSuccess(
          `âœ… Transaction signed successfully! Job created with ID: ${jobId}. 
          Freelancer: ${formData.freelancerAddress.substring(0, 8)}...
          Amount: ${formData.amount} XLM will be locked in escrow.
          
          Note: In production, this transaction would be submitted to the Stellar network.`
        );

        setTimeout(() => {
          navigate("/");
        }, 4000);

      } catch (walletError) {
        console.error("Wallet error:", walletError);
        if (walletError.message && walletError.message.includes("User declined")) {
          throw new Error("âŒ Transaction was rejected in wallet. Please try again and approve the transaction.");
        } else {
          throw new Error(`âŒ Wallet error: ${walletError.message || "Unknown error occurred"}`);
        }
      }

    } catch (err) {
      console.error("Job creation error:", err);
      setError(err.message || "Failed to create job");
    } finally {
      setLoading(false);
    }
  };

  // Helper function to build transaction XDR for wallet signing
  const buildJobCreationTransaction = async () => {
    // For MVP: Build a simple test transaction
    // In production: Build a Soroban contract invocation with create_job parameters
    
    // Return a minimal XDR that Freighter can sign
    // This demonstrates the wallet integration is working
    return new Promise((resolve) => {
      // Simplified XDR for demonstration
      // In a real app, use stellar-sdk to build proper Soroban contract calls
      const mockXDR = "AAAAAgAAAABexSIg06FtXzmFBQQtHZsrnyWxUzmthkBEhs/ktoeVYgAAAGQADKI4AAAABAAAAAEAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAEAAAABAAAAAF7FIiDToW1fOYUFBC0dmyufJbFTOa2GQESGz+S2h5ViAAAACgAAABBGYWlyRGVhbCBDcmVhdGUAAAAAAAAAAAAA";
      setTimeout(() => resolve(mockXDR), 500);
    });
  };

  return (
    <div>
      <div className="card">
        <h2>âž• Create a New Job</h2>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Freelancer Wallet Address *</label>
            <input
              type="text"
              name="freelancerAddress"
              placeholder="GB... or GC..."
              value={formData.freelancerAddress}
              onChange={handleChange}
              required
              pattern="^G[A-Z0-9]{55}$"
            />
            <small style={{ color: "#999" }}>
              The Stellar wallet address of the freelancer
            </small>
          </div>

          <div className="form-group">
            <label>Payment Amount (USD) *</label>
            <input
              type="number"
              name="amount"
              placeholder="e.g., 500"
              value={formData.amount}
              onChange={handleChange}
              min="1"
              step="0.01"
              required
            />
            <small style={{ color: "#999" }}>
              This amount will be locked in escrow until completion
            </small>
          </div>

          <div className="form-group">
            <label>Deadline (days from now) *</label>
            <select
              name="deadlineDays"
              value={formData.deadlineDays}
              onChange={handleChange}
            >
              <option value="1">1 day</option>
              <option value="3">3 days</option>
              <option value="7">7 days</option>
              <option value="14">14 days</option>
              <option value="30">30 days</option>
            </select>
          </div>

          <div className="form-group">
            <label>Job Description *</label>
            <textarea
              name="description"
              placeholder="Describe the work you need done..."
              value={formData.description}
              onChange={handleChange}
              required
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? "Creating Job..." : "Create Job"}
          </button>

          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => navigate("/")}
            style={{ marginLeft: "1rem" }}
          >
            Cancel
          </button>
        </form>

        <div className="info-message" style={{ marginTop: "2rem" }}>
          <strong>ðŸ’¡ How it works:</strong>
          <ol style={{ marginTop: "0.5rem", marginLeft: "1rem" }}>
            <li>Create the job with freelancer details</li>
            <li>Deposit funds to lock them in escrow</li>
            <li>Freelancer submits work</li>
            <li>You review and approve/reject</li>
            <li>On approval, funds are released to freelancer</li>
          </ol>
        </div>
      </div>
    </div>
  );
};

export default CreateJob;
