import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const SubmitWork = ({ wallet }) => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [submissionData, setSubmissionData] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.size > 50 * 1024 * 1024) {
        // 50MB limit
        setError("File size must be less than 50MB");
        return;
      }
      setFile(selectedFile);
      setError("");
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.add("active");
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove("active");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove("active");

    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      setError("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError("Please select a file");
      return;
    }

    setLoading(true);
    setError("");
    setSuccess("");

    try {
      const formData = new FormData();
      formData.append("jobId", jobId);
      formData.append("freelancerAddress", wallet);
      formData.append("file", file);

      const response = await axios.post("/api/jobs/submit-work", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess("Work submitted successfully!");
      setSubmissionData(response.data);
      setFile(null);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        "Failed to submit work"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <button
        className="btn btn-secondary"
        onClick={() => navigate(`/job/${jobId}`)}
        style={{ marginBottom: "1rem" }}
      >
        ‚Üê Back to Job
      </button>

      <div className="card">
        <h2>üì§ Submit Work for Job #{jobId}</h2>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        {!submissionData ? (
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Select File to Upload *</label>

              <div
                className="file-upload-area"
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                onClick={() => document.getElementById("file-input").click()}
              >
                <div style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>
                  üìÅ
                </div>
                <p style={{ fontWeight: "bold", color: "#667eea" }}>
                  {file
                    ? `Selected: ${file.name}`
                    : "Drag and drop your file here or click to browse"}
                </p>
                <small style={{ color: "#999" }}>
                  Supported: Images, PDFs, Documents, Code, Archives (Max 50MB)
                </small>
                <input
                  id="file-input"
                  type="file"
                  onChange={handleFileChange}
                  style={{ display: "none" }}
                />
              </div>
            </div>

            {file && (
              <div
                style={{
                  padding: "1rem",
                  background: "#f5f5f5",
                  borderRadius: "8px",
                  marginBottom: "1rem",
                }}
              >
                <p>
                  <strong>File:</strong> {file.name}
                </p>
                <p>
                  <strong>Size:</strong> {(file.size / 1024 / 1024).toFixed(2)} MB
                </p>
                <p>
                  <strong>Type:</strong> {file.type || "Unknown"}
                </p>
              </div>
            )}

            <div className="info-message">
              <strong>üîê Your work will be processed as follows:</strong>
              <ol style={{ marginTop: "0.5rem", marginLeft: "1rem" }}>
                <li>Original file will be encrypted with AES-256</li>
                <li>A watermarked preview will be created</li>
                <li>Both versions uploaded to IPFS</li>
                <li>Client reviews the preview</li>
                <li>On approval, you receive the original back</li>
              </ol>
            </div>

            <button
              type="submit"
              className="btn btn-primary"
              disabled={!file || loading}
            >
              {loading ? "Uploading..." : "‚úÖ Submit Work"}
            </button>

            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate(`/job/${jobId}`)}
              style={{ marginLeft: "1rem" }}
            >
              Cancel
            </button>
          </form>
        ) : (
          <div className="success-message">
            <h3>‚úÖ Work Submitted Successfully!</h3>

            <div style={{ marginTop: "1.5rem" }}>
              <p>
                <strong>Job ID:</strong> {submissionData.jobId}
              </p>
              <p>
                <strong>Preview CID:</strong> {submissionData.previewCID}
              </p>

              <div className="info-message" style={{ marginTop: "1rem" }}>
                <strong>Preview URL:</strong>
                <div className="preview-url" style={{ marginTop: "0.5rem" }}>
                  https://w3s.link/ipfs/{submissionData.previewCID}
                </div>
                <p style={{ marginTop: "0.5rem", fontSize: "0.9rem" }}>
                  Share this link with the client to review your work. The preview is watermarked for security.
                </p>
              </div>
            </div>

            <button
              className="btn btn-primary"
              onClick={() => navigate(`/job/${jobId}`)}
              style={{ marginTop: "1rem" }}
            >
              Back to Job
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default SubmitWork;
