import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Dashboard = ({ wallet, userRole }) => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("all"); // all, created, submitted

  useEffect(() => {
    // In MVP, we'll load mock data
    // In production, this would call the smart contract
    loadJobs();
  }, []);

  const loadJobs = async () => {
    setLoading(true);
    try {
      // Fetch jobs from backend API
      const response = await axios.get("http://localhost:5000/api/jobs");

      if (response.data.success) {
        setJobs(response.data.jobs);
        console.log(`Loaded ${response.data.count} jobs from backend`);
      } else {
        console.error("Failed to load jobs");
        setJobs([]);
      }
    } catch (error) {
      console.error("Error loading jobs:", error);
      // If backend is not available, show empty state
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusClass = (state) => {
    const states = {
      0: "status-created",
      1: "status-submitted",
      2: "status-approved",
      3: "status-rejected",
      4: "status-refunded",
    };
    return states[state] || "status-created";
  };

  const getStatusLabel = (state) => {
    const labels = {
      0: "Created",
      1: "Submitted",
      2: "Approved",
      3: "Rejected",
      4: "Refunded",
    };
    return labels[state] || "Unknown";
  };

  const filteredJobs = jobs.filter((job) => {
    // Filter by wallet address - show jobs where user is client OR freelancer
    const isMyJob = job.client === wallet || job.freelancer === wallet;

    if (!isMyJob) return false; // Only show jobs relevant to this wallet

    // Then apply status filter
    if (filter === "created") return job.state === 0;
    if (filter === "submitted") return job.state === 1;
    return true; // "all" filter
  });

  return (
    <div>
      <div className="page-header">
        <div className="page-title">
          <h1>Dashboard</h1>
          <p>
            {userRole === "client" ? "Manage your job listings and approvals." : "Find work and manage your active jobs."}
          </p>
        </div>
        {userRole === "client" && (
          <Link to="/create-job" className="btn btn-primary">
            <span>+</span> Create New Job
          </Link>
        )}
      </div>

      <div className="glass-card">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "2rem" }}>
          <h3 style={{ fontSize: "1.2rem" }}>Active Jobs</h3>

          <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
            <label style={{ margin: 0 }}>Filter:</label>
            <select
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              style={{ padding: "0.5rem 1rem", width: "auto" }}
            >
              <option value="all">All Jobs</option>
              <option value="created">Created</option>
              <option value="submitted">Submitted Work</option>
            </select>
          </div>
        </div>

        {loading ? (
          <div className="loading">Loading jobs...</div>
        ) : filteredJobs.length === 0 ? (
          <div style={{ textAlign: "center", padding: "4rem 2rem", color: "var(--text-muted)" }}>
            <div style={{ fontSize: "3rem", marginBottom: "1rem", opacity: 0.5 }}>ðŸ“­</div>
            <p>No jobs found. {userRole === "client" ? "Create one to get started!" : "Wait for clients to post jobs."}</p>
          </div>
        ) : (
          <div className="jobs-grid">
            {filteredJobs.map((job) => (
              <Link key={job.id} to={`/job/${job.id}`} className="job-card-link">
                <div className="glass-card job-card card-hover">
                  <div className="job-header" style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                    <span className="job-id">JOB-{job.id}</span>
                    <span className={`status-badge ${getStatusClass(job.state)}`}>
                      {getStatusLabel(job.state)}
                    </span>
                  </div>

                  <div className="job-amount">
                    ${job.amount} <span>USD</span>
                  </div>

                  <div className="job-description">
                    {job.description}
                  </div>

                  <div style={{ marginTop: "auto", borderTop: "1px solid var(--border)", paddingTop: "1rem", display: "flex", alignItems: "center", gap: "0.5rem", color: "var(--text-muted)", fontSize: "0.85rem" }}>
                    <span>ðŸ“… Due:</span> {new Date(job.deadline).toLocaleDateString()}
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
