import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const JobDetail = ({ wallet, userRole }) => {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(null);
  const [jobStatus, setJobStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [approving, setApproving] = useState(false);
  const [rejecting, setRejecting] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    loadJobDetails();
  }, [jobId]);

  const loadJobDetails = async () => {
    try {
      // Fetch real job data from backend
      const response = await axios.get(`http://localhost:5000/api/jobs/${jobId}`);

      if (response.data.success) {
        setJob(response.data.job);
        console.log("Loaded job:", response.data.job);
      } else {
        setError("Job not found");
        setJob(null);
      }

      // Load submission status from backend
      try {
        const statusResponse = await axios.get(`http://localhost:5000/api/jobs/${jobId}/preview`);
        setJobStatus(statusResponse.data);
      } catch (err) {
        // Not submitted yet
        setJobStatus(null);
      }
    } catch (err) {
      console.error("Error loading job:", err);
      setError("Failed to load job details");
      setJob(null);
    } finally {
      setLoading(false);
    }
  };

  const getStateLabel = (state) => {
    const labels = {
      0: "Created",
      1: "Submitted",
      2: "Approved",
      3: "Rejected",
      4: "Refunded",
    };
    return labels[state] || "Unknown";
  };

  const handleApprove = async () => {
    setApproving(true);
    try {
      const response = await axios.post(`/api/jobs/${jobId}/approve`);

      alert(`Job approved! Original file CID: ${response.data.originalCID}`);
      alert(`Decryption Key: ${response.data.decryptionKey}`);

      // Reload job status
      loadJobDetails();
    } catch (err) {
      setError("Failed to approve job: " + err.message);
    } finally {
      setApproving(false);
    }
  };

  const handleReject = async () => {
    if (window.confirm("Are you sure you want to reject this job?")) {
      setRejecting(true);
      try {
        await axios.post(`/api/jobs/${jobId}/reject`);
        alert("Job rejected. The original file will not be revealed.");
        loadJobDetails();
      } catch (err) {
        setError("Failed to reject job: " + err.message);
      } finally {
        setRejecting(false);
      }
    }
  };

  if (loading) return <div className="loading">Loading job details...</div>;
  if (!job) return <div className="error-message">Job not found</div>;

  const isClient = wallet === job.client || userRole === "client";
  const isFreelancer = wallet === job.freelancer || userRole === "freelancer";

  return (
    <div>
      <button
        className="btn btn-secondary"
        onClick={() => navigate("/")}
        style={{ marginBottom: "1rem" }}
      >
        ‚Üê Back to Dashboard
      </button>

      <div className="card">
        <h2>Job #{jobId}</h2>

        {error && <div className="error-message">{error}</div>}

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
            gap: "1.5rem",
            marginBottom: "1.5rem",
          }}
        >
          <div>
            <label style={{ color: "#999", fontSize: "0.9rem" }}>Amount</label>
            <div style={{ fontSize: "1.5rem", color: "#4caf50", fontWeight: "bold" }}>
              ${job.amount}
            </div>
          </div>

          <div>
            <label style={{ color: "#999", fontSize: "0.9rem" }}>Status</label>
            <div style={{ fontSize: "1.1rem", fontWeight: "bold", color: "#667eea" }}>
              {getStateLabel(job.state)}
            </div>
          </div>

          <div>
            <label style={{ color: "#999", fontSize: "0.9rem" }}>Deadline</label>
            <div style={{ fontSize: "1rem" }}>
              ‚è∞ {new Date(job.deadline).toLocaleDateString()}
            </div>
          </div>
        </div>

        <div style={{ marginBottom: "1.5rem" }}>
          <label style={{ fontWeight: "bold", color: "#333" }}>Description</label>
          <p style={{ marginTop: "0.5rem", color: "#666", lineHeight: "1.6" }}>
            {job.description}
          </p>
        </div>

        <div
          style={{
            padding: "1rem",
            background: "#f5f5f5",
            borderRadius: "8px",
            fontSize: "0.9rem",
          }}
        >
          <p>
            <strong>Client:</strong> {job.client}
          </p>
          <p>
            <strong>Freelancer:</strong> {job.freelancer}
          </p>
          <p>
            <strong>Created:</strong> {new Date(job.createdAt).toLocaleString()}
          </p>
          {job.submittedAt && (
            <p>
              <strong>Submitted:</strong> {new Date(job.submittedAt).toLocaleString()}
            </p>
          )}
        </div>
      </div>

      {/* Submission Preview (for Client) */}
      {isClient && jobStatus && (
        <div className="card">
          <h3>üìÅ Work Submission</h3>

          <div className="info-message">
            Freelancer has submitted work. Review the preview before approving.
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <label style={{ fontWeight: "bold" }}>Preview</label>
            <p style={{ color: "#666", marginBottom: "0.5rem" }}>
              Submitted: {jobStatus.submittedAt}
            </p>
            <a
              href={jobStatus.previewURL}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-primary"
            >
              üëÄ View Preview
            </a>
          </div>

          <div
            style={{
              display: "flex",
              gap: "1rem",
              marginTop: "1.5rem",
            }}
          >
            <button
              className="btn btn-success"
              onClick={handleApprove}
              disabled={approving || job.state !== 1}
            >
              {approving ? "Approving..." : "‚úÖ Approve & Release Funds"}
            </button>
            <button
              className="btn btn-danger"
              onClick={handleReject}
              disabled={rejecting || job.state !== 1}
            >
              {rejecting ? "Rejecting..." : "‚ùå Reject & Refund"}
            </button>
          </div>
        </div>
      )}

      {/* Submit Work (for Freelancer) */}
      {isFreelancer && job.state === 0 && (
        <div className="card">
          <h3>üì§ Submit Your Work</h3>
          <button
            className="btn btn-primary"
            onClick={() => navigate(`/job/${jobId}/submit`)}
          >
            Upload Work File
          </button>
        </div>
      )}

      {/* Work Submitted (for Freelancer) */}
      {isFreelancer && job.state === 1 && (
        <div className="card" style={{ background: "#fff3e0" }}>
          <h3>‚è≥ Waiting for Client Review</h3>
          <p style={{ color: "#f57c00", fontWeight: "bold" }}>
            Your work has been submitted and is awaiting client approval.
          </p>
        </div>
      )}

      {/* Approved */}
      {job.state === 2 && (
        <div className="card" style={{ background: "#e8f5e9" }}>
          <h3>‚úÖ Job Approved</h3>
          <p style={{ color: "#388e3c", fontWeight: "bold" }}>
            This job has been approved and funds have been released to the freelancer.
          </p>
        </div>
      )}

      {/* Rejected */}
      {job.state === 3 && (
        <div className="card" style={{ background: "#ffebee" }}>
          <h3>‚ùå Job Rejected</h3>
          <p style={{ color: "#d32f2f", fontWeight: "bold" }}>
            This job has been rejected and funds have been refunded to the client.
          </p>
        </div>
      )}
    </div>
  );
};

export default JobDetail;
