import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const WalletConnect = ({ onConnect }) => {
  const [selectedRole, setSelectedRole] = useState("client");
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Check if Freighter is available
      if (typeof window !== "undefined" && window.freighter) {
        const publicKey = await window.freighter.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else if (typeof window !== "undefined" && window.stellar) {
        const publicKey = await window.stellar.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else {
        // Allow manual entry as a fallback for testing without the browser extension
        const useFallback = window.confirm(
          "Freighter or Stellar Wallet not installed. Would you like to enter a mock wallet address to continue?"
        );
        if (useFallback) {
          const mock = prompt("Enter a Stellar public key (or leave blank for a generated mock):");
          let publicKey = mock;
          if (!publicKey) {
            // generate a fake-looking public key for UI/testing only
            publicKey = "G" + Math.random().toString(36).substring(2, 56).toUpperCase().padEnd(56, "A").slice(0, 56);
          }
          onConnect(publicKey, selectedRole);
          navigate("/");
        }
      }
    } catch (error) {
      console.error("Wallet connection failed:", error);
      alert("Failed to connect wallet: " + error.message);
    }
  };

  return (
    <div className="wallet-page-container" style={{ background: "radial-gradient(circle at top, #eff6ff 0%, #ffffff 100%)", minHeight: "calc(100vh - 80px)", alignItems: "flex-start", paddingTop: "4rem" }}>
      <div className="wallet-connect-card" style={{ maxWidth: "480px", width: "100%", padding: "0 1.5rem" }}>

        <div style={{ textAlign: "center", marginBottom: "3rem" }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "800", color: "var(--text-main)", marginBottom: "1rem", letterSpacing: "-0.03em" }}>
            Sign in to FairDeal.
          </h1>
          <p style={{ fontSize: "1.1rem", color: "var(--text-muted)", lineHeight: "1.5" }}>
            Secure, trustless escrow for the future of work. <br />
            Select your role to continue.
          </p>
        </div>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1.5rem", marginBottom: "2.5rem" }}>
          <button
            onClick={() => setSelectedRole("client")}
            style={{
              padding: "2rem 1.5rem",
              borderRadius: "16px",
              border: selectedRole === "client" ? "2px solid var(--primary)" : "1px solid var(--border)",
              background: selectedRole === "client" ? "#eff6ff" : "white",
              cursor: "pointer",
              transition: "all 0.2s ease",
              textAlign: "center",
              boxShadow: selectedRole === "client" ? "0 10px 25px -5px rgba(37, 99, 235, 0.15)" : "0 4px 6px -1px rgba(0,0,0,0.05)",
              transform: selectedRole === "client" ? "translateY(-2px)" : "none"
            }}
          >
            <div style={{ fontWeight: "700", color: selectedRole === "client" ? "var(--primary)" : "var(--text-main)", fontSize: "1.2rem", marginBottom: "0.5rem" }}>Client</div>
            <div style={{ fontSize: "0.9rem", color: "var(--text-muted)" }}>I want to hire talent</div>
          </button>

          <button
            onClick={() => setSelectedRole("freelancer")}
            style={{
              padding: "2rem 1.5rem",
              borderRadius: "16px",
              border: selectedRole === "freelancer" ? "2px solid var(--primary)" : "1px solid var(--border)",
              background: selectedRole === "freelancer" ? "#eff6ff" : "white",
              cursor: "pointer",
              transition: "all 0.2s ease",
              textAlign: "center",
              boxShadow: selectedRole === "freelancer" ? "0 10px 25px -5px rgba(37, 99, 235, 0.15)" : "0 4px 6px -1px rgba(0,0,0,0.05)",
              transform: selectedRole === "freelancer" ? "translateY(-2px)" : "none"
            }}
          >
            <div style={{ fontWeight: "700", color: selectedRole === "freelancer" ? "var(--primary)" : "var(--text-main)", fontSize: "1.2rem", marginBottom: "0.5rem" }}>Freelancer</div>
            <div style={{ fontSize: "0.9rem", color: "var(--text-muted)" }}>I want to find work</div>
          </button>
        </div>

        <button
          className="btn btn-primary"
          onClick={connectWallet}
          style={{ width: "100%", padding: "1.25rem", fontSize: "1.1rem", borderRadius: "12px", marginBottom: "2rem" }}
        >
          Connect Freighter Wallet
        </button>

        <div style={{ textAlign: "center" }}>
          <p style={{ fontSize: "0.9rem", color: "var(--text-muted)" }}>
            New to Stellar? <a href="https://freighter.app" target="_blank" rel="noopener noreferrer" style={{ color: "var(--primary)", fontWeight: "600" }}>Install Freighter</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default WalletConnect;
