import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const WalletConnect = ({ onConnect }) => {
  const [selectedRole, setSelectedRole] = useState("client");
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Check if Freighter is available
      if (typeof window !== "undefined" && window.freighter) {
        const publicKey = await window.freighter.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else if (typeof window !== "undefined" && window.stellar) {
        const publicKey = await window.stellar.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else {
        alert(
          "Freighter or Stellar Wallet not installed. Please install it from https://freighter.app or use Stellar Wallet."
        );
      }
    } catch (error) {
      console.error("Wallet connection failed:", error);
      alert("Failed to connect wallet: " + error.message);
    }
  };

  return (
    <div className="wallet-connect-page">
      <div className="wallet-card">
        <h1>üöÄ FairDeal Escrow</h1>
        <p>Connect your Stellar wallet to get started</p>

        <div className="form-group">
          <label>I am a:</label>
          <div className="role-selector">
            <button
              className={`role-btn ${selectedRole === "client" ? "active" : ""}`}
              onClick={() => setSelectedRole("client")}
            >
              üë®‚Äçüíº Client
            </button>
            <button
              className={`role-btn ${selectedRole === "freelancer" ? "active" : ""}`}
              onClick={() => setSelectedRole("freelancer")}
            >
              üë®‚Äçüíª Freelancer
            </button>
          </div>
        </div>

        <button className="btn btn-primary" onClick={connectWallet}>
          Connect {selectedRole === "client" ? "as Client" : "as Freelancer"}
        </button>

        <p style={{ marginTop: "1.5rem", fontSize: "0.9rem", color: "#999" }}>
          üí° Make sure you have{" "}
          <a href="https://freighter.app" target="_blank" rel="noopener noreferrer" style={{ color: "#667eea" }}>
            Freighter
          </a>{" "}
          or Stellar Wallet installed.
        </p>
      </div>
    </div>
  );
};

export default WalletConnect;
