import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const WalletConnect = ({ onConnect }) => {
  const [selectedRole, setSelectedRole] = useState("client");
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Check if Freighter is available
      if (typeof window !== "undefined" && window.freighter) {
        const publicKey = await window.freighter.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else if (typeof window !== "undefined" && window.stellar) {
        const publicKey = await window.stellar.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else {
        // Allow manual entry as a fallback for testing without the browser extension
        const useFallback = window.confirm(
          "Freighter or Stellar Wallet not installed. Would you like to enter a mock wallet address to continue?"
        );
        if (useFallback) {
          const mock = prompt("Enter a Stellar public key (or leave blank for a generated mock):");
          let publicKey = mock;
          if (!publicKey) {
            // generate a fake-looking public key for UI/testing only
            publicKey = "G" + Math.random().toString(36).substring(2, 56).toUpperCase().padEnd(56, "A").slice(0, 56);
          }
          onConnect(publicKey, selectedRole);
          navigate("/");
        }
      }
    } catch (error) {
      console.error("Wallet connection failed:", error);
      alert("Failed to connect wallet: " + error.message);
    }
  };

  return (
    <div className="wallet-page-container" style={{
      background: "var(--bg-alt)",
      minHeight: "calc(100vh - 80px)",
      padding: "2rem",
      display: "flex",
      alignItems: "center",
      justifyContent: "center"
    }}>
      <div style={{
        display: "flex",
        width: "100%",
        maxWidth: "1200px",
        background: "#fff",
        borderRadius: "32px",
        overflow: "hidden",
        boxShadow: "0 20px 40px rgba(0,0,0,0.08)",
        minHeight: "700px"
      }}>

        {/* Left Panel - Typography */}
        <div style={{
          flex: 1,
          padding: "5rem",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center"
        }}>
          <div style={{
            fontSize: "0.9rem",
            fontWeight: "700",
            letterSpacing: "0.1em",
            color: "var(--text-muted)",
            textTransform: "uppercase",
            marginBottom: "1rem"
          }}>
            FairDeal Escrow
          </div>

          <h1 style={{
            fontSize: "4.5rem",
            fontWeight: "800",
            lineHeight: "1.1",
            marginBottom: "2rem",
            color: "#1a1a1a"
          }}>
            POWERED BY <br />
            <span style={{ color: "var(--primary)" }}>STELLAR</span> SMART <br />
            CONTRACTS.
          </h1>

          <p style={{
            fontSize: "1.2rem",
            color: "var(--text-muted)",
            maxWidth: "450px",
            marginBottom: "3rem",
            lineHeight: "1.7"
          }}>
            Experience the future of freelance work. <br />
            Trustless payments, instant settlement, and zero platform fees.
          </p>

          <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
            <div style={{ fontSize: "0.9rem", fontWeight: "600", borderBottom: "2px solid #000", paddingBottom: "2px" }}>
              Join the revolution
            </div>
            <div style={{ fontSize: "1.2rem" }}>â†’</div>
          </div>
        </div>

        {/* Right Panel - Stellar Visual & Login Card */}
        <div style={{
          flex: 1,
          position: "relative",
          background: "linear-gradient(135deg, #0f172a 0%, #1e1e2e 100%)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "2rem"
        }}>
          {/* Abstract Stellar Background Pattern */}
          <div style={{
            position: "absolute",
            top: 0, left: 0, right: 0, bottom: 0,
            opacity: 0.3,
            backgroundImage: "radial-gradient(circle at 20% 20%, rgba(255,255,255,0.1) 1px, transparent 1px), radial-gradient(circle at 80% 80%, rgba(255,255,255,0.1) 1px, transparent 1px)",
            backgroundSize: "40px 40px"
          }}></div>

          {/* Glowing Orb */}
          <div style={{
            position: "absolute",
            top: "-10%",
            right: "-10%",
            width: "500px",
            height: "500px",
            background: "radial-gradient(circle, rgba(37, 99, 235, 0.4) 0%, transparent 70%)",
            borderRadius: "50%",
            filter: "blur(40px)"
          }}></div>

          {/* Floating Login Card */}
          <div style={{
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(10px)",
            padding: "2.5rem",
            borderRadius: "24px",
            width: "100%",
            maxWidth: "400px",
            boxShadow: "0 25px 50px -12px rgba(0, 0, 0, 0.25)",
            position: "relative",
            zIndex: 10
          }}>
            <h2 style={{ fontSize: "1.5rem", fontWeight: "700", textAlign: "center", marginBottom: "0.5rem", color: "#000" }}>Connect your wallet</h2>
            <p style={{ textAlign: "center", color: "var(--text-muted)", marginBottom: "2rem", fontSize: "0.95rem" }}>
              Access your FairDeal dashboard
            </p>

            <div className="form-group" style={{ marginBottom: "1.5rem" }}>
              <label style={{ fontSize: "0.8rem", fontWeight: "700", color: "#666", marginBottom: "0.5rem", display: "block", textTransform: "uppercase" }}>Select Role</label>
              <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "10px" }}>
                <button
                  onClick={() => setSelectedRole("client")}
                  style={{
                    padding: "1rem",
                    borderRadius: "12px",
                    border: selectedRole === "client" ? "2px solid var(--primary)" : "1px solid #e5e5e5",
                    background: selectedRole === "client" ? "var(--primary-light)" : "white",
                    fontWeight: "600",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    gap: "0.5rem",
                    cursor: "pointer",
                    transition: "all 0.2s"
                  }}
                >
                  <span style={{ fontSize: "1.2rem" }}>ðŸ‘”</span>
                  <span>Client</span>
                </button>
                <button
                  onClick={() => setSelectedRole("freelancer")}
                  style={{
                    padding: "1rem",
                    borderRadius: "12px",
                    border: selectedRole === "freelancer" ? "2px solid var(--primary)" : "1px solid #e5e5e5",
                    background: selectedRole === "freelancer" ? "var(--primary-light)" : "white",
                    fontWeight: "600",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    gap: "0.5rem",
                    cursor: "pointer",
                    transition: "all 0.2s"
                  }}
                >
                  <span style={{ fontSize: "1.2rem" }}>ðŸ’»</span>
                  <span>Freelancer</span>
                </button>
              </div>
            </div>

            <button
              className="btn btn-primary"
              onClick={connectWallet}
              style={{ width: "100%", padding: "1rem", borderRadius: "12px", fontWeight: "700", fontSize: "1rem" }}
            >
              Connect Freighter
            </button>

            <div style={{ marginTop: "1.5rem", padding: "1rem", background: "#f8fafc", borderRadius: "12px", display: "flex", alignItems: "center", gap: "0.75rem" }}>
              <div style={{ width: "32px", height: "32px", borderRadius: "8px", background: "#000", flexShrink: 0 }}></div>
              <div style={{ fontSize: "0.85rem", color: "#64748b", lineHeight: "1.4" }}>
                <strong>Secure Escrow</strong><br />
                Powered by Soroban Network
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WalletConnect;
