import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const CreateJob = ({ wallet }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [formData, setFormData] = useState({
    freelancerAddress: "",
    amount: "",
    deadlineDays: "7",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    try {
      // Validation
      if (!formData.freelancerAddress) {
        throw new Error("Freelancer address is required");
      }
      if (!formData.amount || formData.amount <= 0) {
        throw new Error("Amount must be greater than 0");
      }
      if (!formData.description) {
        throw new Error("Description is required");
      }

      // In MVP, we'll simulate contract interaction
      // In production: call Soroban smart contract via Freighter
      console.log("Creating job with:", formData);

      // Simulate contract call
      const jobId = Math.floor(Math.random() * 10000);

      setSuccess(
        `Job created successfully! Job ID: ${jobId}. Please deposit funds to activate the escrow.`
      );

      setTimeout(() => {
        navigate("/");
      }, 2000);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div className="card">
        <h2>âž• Create a New Job</h2>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Freelancer Wallet Address *</label>
            <input
              type="text"
              name="freelancerAddress"
              placeholder="GB... or GC..."
              value={formData.freelancerAddress}
              onChange={handleChange}
              required
              pattern="^G[A-Z0-9]{55}$"
            />
            <small style={{ color: "#999" }}>
              The Stellar wallet address of the freelancer
            </small>
          </div>

          <div className="form-group">
            <label>Payment Amount (USD) *</label>
            <input
              type="number"
              name="amount"
              placeholder="e.g., 500"
              value={formData.amount}
              onChange={handleChange}
              min="1"
              step="0.01"
              required
            />
            <small style={{ color: "#999" }}>
              This amount will be locked in escrow until completion
            </small>
          </div>

          <div className="form-group">
            <label>Deadline (days from now) *</label>
            <select
              name="deadlineDays"
              value={formData.deadlineDays}
              onChange={handleChange}
            >
              <option value="1">1 day</option>
              <option value="3">3 days</option>
              <option value="7">7 days</option>
              <option value="14">14 days</option>
              <option value="30">30 days</option>
            </select>
          </div>

          <div className="form-group">
            <label>Job Description *</label>
            <textarea
              name="description"
              placeholder="Describe the work you need done..."
              value={formData.description}
              onChange={handleChange}
              required
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? "Creating Job..." : "Create Job"}
          </button>

          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => navigate("/")}
            style={{ marginLeft: "1rem" }}
          >
            Cancel
          </button>
        </form>

        <div className="info-message" style={{ marginTop: "2rem" }}>
          <strong>ðŸ’¡ How it works:</strong>
          <ol style={{ marginTop: "0.5rem", marginLeft: "1rem" }}>
            <li>Create the job with freelancer details</li>
            <li>Deposit funds to lock them in escrow</li>
            <li>Freelancer submits work</li>
            <li>You review and approve/reject</li>
            <li>On approval, funds are released to freelancer</li>
          </ol>
        </div>
      </div>
    </div>
  );
};

export default CreateJob;
