import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import * as StellarSdk from '@stellar/stellar-sdk';

const CreateJob = ({ wallet }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [formData, setFormData] = useState({
    freelancerAddress: "",
    amount: "",
    deadlineDays: "7",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    try {
      // Validation
      if (!formData.freelancerAddress) {
        throw new Error("Freelancer address is required");
      }
      if (!formData.amount || formData.amount <= 0) {
        throw new Error("Amount must be greater than 0");
      }
      if (!formData.description) {
        throw new Error("Description is required");
      }

      // Check if wallet is connected
      if (!wallet) {
        throw new Error("Please connect your wallet first");
      }

      console.log("Creating job with:", formData);
      console.log("Client wallet:", wallet);

      // Calculate deadline timestamp (Unix timestamp in seconds)
      const deadlineDate = new Date();
      deadlineDate.setDate(deadlineDate.getDate() + parseInt(formData.deadlineDays));
      const deadlineTimestamp = Math.floor(deadlineDate.getTime() / 1000);

      setSuccess("â³ Creating job on smart contract...");

      // Connect to Stellar testnet using static import
      const server = new StellarSdk.SorobanRpc.Server('https://soroban-testnet.stellar.org');

      // Get account info
      const sourceAccount = await server.getAccount(wallet);

      // Contract address
      const contractAddress = process.env.REACT_APP_CONTRACT_ID || 'CBMWBQ7SSWM2JRUVSBWHC2BL2GGR6HS';
      const contract = new StellarSdk.Contract(contractAddress);

      // Convert amount to stroops (1 XLM = 10,000,000 stroops)
      const amountInStroops = Math.floor(parseFloat(formData.amount) * 10000000);

      // Build contract invocation transaction
      const transaction = new StellarSdk.TransactionBuilder(sourceAccount, {
        fee: StellarSdk.BASE_FEE,
        networkPassphrase: StellarSdk.Networks.TESTNET,
      })
        .addOperation(
          contract.call(
            'create_job',
            new StellarSdk.Address(wallet).toScVal(),
            new StellarSdk.Address(formData.freelancerAddress).toScVal(),
            StellarSdk.nativeToScVal(amountInStroops, { type: 'i128' }),
            StellarSdk.nativeToScVal(deadlineTimestamp, { type: 'u64' }),
            StellarSdk.nativeToScVal(formData.description, { type: 'string' })
          )
        )
        .setTimeout(300)
        .build();

      console.log("Transaction built, requesting signature...");

      // Sign with Freighter
      const signedTx = await window.freighter.signTransaction(
        transaction.toXDR(),
        StellarSdk.Networks.TESTNET
      );

      setSuccess("â³ Submitting to blockchain...");

      // Submit to network
      const txResponse = await server.sendTransaction(
        new StellarSdk.Transaction(signedTx, StellarSdk.Networks.TESTNET)
      );

      console.log("Transaction submitted:", txResponse.hash);

      // Wait for confirmation
      let status = await server.getTransaction(txResponse.hash);
      while (status.status === 'PENDING' || status.status === 'NOT_FOUND') {
        await new Promise(resolve => setTimeout(resolve, 1000));
        status = await server.getTransaction(txResponse.hash);
      }

      if (status.status === 'SUCCESS') {
        // Get the job ID from the contract response
        const jobId = Date.now(); // Temporary - parse from contract return value

        // Save to backend for tracking
        await axios.post("http://localhost:5000/api/jobs", {
          client: wallet,
          freelancer: formData.freelancerAddress,
          amount: formData.amount,
          deadline: deadlineDate.toISOString(),
          description: formData.description,
          contractJobId: jobId,
          txHash: txResponse.hash,
        });

        setSuccess(
          `âœ… Job created on blockchain!\n\n` +
          `ðŸ” Funds locked in smart contract\n` +
          `ðŸ’° Amount: ${formData.amount} XLM\n` +
          `ðŸ“… Deadline: ${formData.deadlineDays} days\n` +
          `ðŸ”— TX: ${txResponse.hash.substring(0, 12)}...\n\n` +
          `Redirecting to dashboard...`
        );

        setTimeout(() => {
          navigate("/");
        }, 3000);
      } else {
        throw new Error(`Transaction failed: ${status.status}`);
      }

    } catch (err) {
      console.error("Error creating job:", err);
      setError(err.message || "Failed to create job");
    } finally {
      setLoading(false);
    }
  };

  // Helper function to build transaction XDR for wallet signing
  const buildJobCreationTransaction = async () => {
    // For MVP: Build a simple test transaction
    // In production: Build a Soroban contract invocation with create_job parameters

    // Return a minimal XDR that Freighter can sign
    // This demonstrates the wallet integration is working
    return new Promise((resolve) => {
      // Simplified XDR for demonstration
      // In a real app, use stellar-sdk to build proper Soroban contract calls
      const mockXDR = "AAAAAgAAAABexSIg06FtXzmFBQQtHZsrnyWxUzmthkBEhs/ktoeVYgAAAGQADKI4AAAABAAAAAEAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAEAAAABAAAAAF7FIiDToW1fOYUFBC0dmyufJbFTOa2GQESGz+S2h5ViAAAACgAAABBGYWlyRGVhbCBDcmVhdGUAAAAAAAAAAAAA";
      setTimeout(() => resolve(mockXDR), 500);
    });
  };

  return (
    <div>
      <div className="card">
        <h2>âž• Create a New Job</h2>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Freelancer Wallet Address *</label>
            <input
              type="text"
              name="freelancerAddress"
              placeholder="GB... or GC..."
              value={formData.freelancerAddress}
              onChange={handleChange}
              required
              pattern="^G[A-Z0-9]{55}$"
            />
            <small style={{ color: "#999" }}>
              The Stellar wallet address of the freelancer
            </small>
          </div>

          <div className="form-group">
            <label>Payment Amount (USD) *</label>
            <input
              type="number"
              name="amount"
              placeholder="e.g., 500"
              value={formData.amount}
              onChange={handleChange}
              min="1"
              step="0.01"
              required
            />
            <small style={{ color: "#999" }}>
              This amount will be locked in escrow until completion
            </small>
          </div>

          <div className="form-group">
            <label>Deadline (days from now) *</label>
            <select
              name="deadlineDays"
              value={formData.deadlineDays}
              onChange={handleChange}
            >
              <option value="1">1 day</option>
              <option value="3">3 days</option>
              <option value="7">7 days</option>
              <option value="14">14 days</option>
              <option value="30">30 days</option>
            </select>
          </div>

          <div className="form-group">
            <label>Job Description *</label>
            <textarea
              name="description"
              placeholder="Describe the work you need done..."
              value={formData.description}
              onChange={handleChange}
              required
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? "Creating Job..." : "Create Job"}
          </button>

          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => navigate("/")}
            style={{ marginLeft: "1rem" }}
          >
            Cancel
          </button>
        </form>

        <div className="info-message" style={{ marginTop: "2rem" }}>
          <strong>ðŸ’¡ How it works:</strong>
          <ol style={{ marginTop: "0.5rem", marginLeft: "1rem" }}>
            <li>Create the job with freelancer details</li>
            <li>Deposit funds to lock them in escrow</li>
            <li>Freelancer submits work</li>
            <li>You review and approve/reject</li>
            <li>On approval, funds are released to freelancer</li>
          </ol>
        </div>
      </div>
    </div>
  );
};

export default CreateJob;
