import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const WalletConnect = ({ onConnect }) => {
  const [selectedRole, setSelectedRole] = useState("client");
  const navigate = useNavigate();

  const connectWallet = async () => {
    try {
      // Check if Freighter is available
      if (typeof window !== "undefined" && window.freighter) {
        const publicKey = await window.freighter.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else if (typeof window !== "undefined" && window.stellar) {
        const publicKey = await window.stellar.getPublicKey();
        onConnect(publicKey, selectedRole);
        navigate("/");
      } else {
        // Allow manual entry as a fallback for testing without the browser extension
        const useFallback = window.confirm(
          "Freighter or Stellar Wallet not installed. Would you like to enter a mock wallet address to continue?"
        );
        if (useFallback) {
          const mock = prompt("Enter a Stellar public key (or leave blank for a generated mock):");
          let publicKey = mock;
          if (!publicKey) {
            // generate a fake-looking public key for UI/testing only
            publicKey = "G" + Math.random().toString(36).substring(2, 56).toUpperCase().padEnd(56, "A").slice(0, 56);
          }
          onConnect(publicKey, selectedRole);
          navigate("/");
        }
      }
    } catch (error) {
      console.error("Wallet connection failed:", error);
      alert("Failed to connect wallet: " + error.message);
    }
  };

  return (
    <div style={{ display: "flex", minHeight: "calc(100vh - 80px)" }}>
      {/* Left Brand Panel */}
      <div style={{
        flex: 1,
        background: "linear-gradient(135deg, #0f172a 0%, #1e3a8a 100%)",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        padding: "4rem",
        position: "relative",
        overflow: "hidden"
      }}>
        {/* Decorative circle */}
        <div style={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: "600px",
          height: "600px",
          background: "radial-gradient(circle, rgba(255,255,255,0.05) 0%, rgba(255,255,255,0) 70%)",
          borderRadius: "50%"
        }}></div>

        <div style={{ position: "relative", zIndex: 1 }}>
          <h1 style={{ fontSize: "3.5rem", marginBottom: "1.5rem", fontWeight: "800", lineHeight: "1.1" }}>
            FairDeal.
          </h1>
          <p style={{ fontSize: "1.5rem", opacity: 0.9, fontWeight: "300", maxWidth: "400px", lineHeight: "1.5" }}>
            The trustless escrow platform for the gig economy. <br />
            <span style={{ opacity: 0.6, fontSize: "1.1rem" }}>Built on Stellar.</span>
          </p>

          <div style={{ marginTop: "3rem", display: "flex", gap: "1rem" }}>
            <div style={{ background: "rgba(255,255,255,0.1)", padding: "1rem", borderRadius: "8px", width: "120px" }}>
              <div style={{ fontSize: "1.5rem", fontWeight: "bold" }}>$0</div>
              <div style={{ fontSize: "0.8rem", opacity: 0.7 }}>Platform Fees</div>
            </div>
            <div style={{ background: "rgba(255,255,255,0.1)", padding: "1rem", borderRadius: "8px", width: "120px" }}>
              <div style={{ fontSize: "1.5rem", fontWeight: "bold" }}>1-3s</div>
              <div style={{ fontSize: "0.8rem", opacity: 0.7 }}>Settlement</div>
            </div>
          </div>
        </div>
      </div>

      {/* Right Login Panel */}
      <div style={{
        flex: 1,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "white",
        padding: "2rem"
      }}>
        <div style={{ width: "100%", maxWidth: "420px" }}>

          <div style={{ marginBottom: "2.5rem" }}>
            <h2 style={{ fontSize: "1.8rem", fontWeight: "800", marginBottom: "0.5rem", color: "var(--text-main)" }}>Sign in</h2>
            <p style={{ color: "var(--text-muted)" }}>Connect your wallet to continue.</p>
          </div>

          <div className="form-group" style={{ marginBottom: "2rem" }}>
            <label style={{ fontSize: "0.85rem", fontWeight: "600", color: "var(--text-main)", marginBottom: "0.75rem", display: "block" }}>I AM CONNECTING AS A</label>
            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
              <button
                onClick={() => setSelectedRole("client")}
                style={{
                  padding: "1rem",
                  borderRadius: "8px",
                  border: selectedRole === "client" ? "2px solid var(--primary)" : "1px solid var(--border)",
                  background: selectedRole === "client" ? "var(--primary-light)" : "white",
                  color: selectedRole === "client" ? "var(--primary)" : "var(--text-muted)",
                  fontWeight: "600",
                  cursor: "pointer",
                  textAlign: "center",
                  transition: "all 0.2s"
                }}
              >
                Client
              </button>
              <button
                onClick={() => setSelectedRole("freelancer")}
                style={{
                  padding: "1rem",
                  borderRadius: "8px",
                  border: selectedRole === "freelancer" ? "2px solid var(--primary)" : "1px solid var(--border)",
                  background: selectedRole === "freelancer" ? "var(--primary-light)" : "white",
                  color: selectedRole === "freelancer" ? "var(--primary)" : "var(--text-muted)",
                  fontWeight: "600",
                  cursor: "pointer",
                  textAlign: "center",
                  transition: "all 0.2s"
                }}
              >
                Freelancer
              </button>
            </div>
          </div>

          <button
            className="btn btn-primary"
            onClick={connectWallet}
            style={{
              width: "100%",
              padding: "1rem",
              fontSize: "1rem",
              borderRadius: "8px",
              boxShadow: "0 4px 12px rgba(37, 99, 235, 0.2)"
            }}
          >
            Connect Wallet
          </button>

          <p style={{ marginTop: "2rem", fontSize: "0.9rem", color: "var(--text-muted)", textAlign: "center" }}>
            By connecting, you agree to the <a href="#" style={{ textDecoration: "underline" }}>Terms of Escrow</a>.
          </p>

          <div style={{ marginTop: "1rem", textAlign: "center", fontSize: "0.85rem", color: "var(--text-muted)" }}>
            New? <a href="https://freighter.app" target="_blank" rel="noopener noreferrer" style={{ color: "var(--primary)", fontWeight: "600" }}>Get Freighter Wallet</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WalletConnect;
